mapper.select:
select * from user


        and email = #{email}


        and username = #{username}

    and firm_name = #{firmName}

mapper.update:
UPDATE user
username = #{username}, firm_name = #{firmName}, work_type = #{workType}, image = #{image}, email = #{email}, account_status = #{accountStatus}, telephone = #{telephone}, password = #{password}, resume = #{resume}, update_time = #{updateTime}, age = #{age}, id_number = #{IDNumber},
WHERE id = #{id}
